import React, { Component } from "react";
import Cell from "./Cell";
import Title from "./Title";
import './Board.css';

class Board extends Component {
  static defaultProps = {
    nrows: 5,
    ncols: 5,
    chanceLightStartsOn: 0.25
  }
  constructor( props ) {
    super( props );

    // TODO: set initial state
    this.state = {
      hasWon: false,
      helpAsked: false,
      board: this.createBoard(),
      moves: 0
    }
  }

  createBoard() {
    let board = [];
    for ( let y = 0; y < this.props.nrows; y++ ) {
      let row = [];
      for ( let x = 0; x < this.props.ncols; x++ ) {
        row.push( Math.random() < this.props.chanceLightStartsOn )
      }
      board.push( row );
    }
    return board;
  }

  askHelp() {
    let helpAsked = this.state.helpAsked
    this.setState( { helpAsked: !helpAsked } )
    console.log( 'HELP!' )
  }

  flipCellsAround( coord ) {
    let { ncols, nrows } = this.props;
    let board = this.state.board;
    let moves = this.state.moves;
    let [ y, x ] = coord.split( "-" ).map( Number );


    function flipCell( y, x ) {
      // if this coord is actually on board, flip it

      if ( x >= 0 && x < ncols && y >= 0 && y < nrows ) {
        board[ y ][ x ] = !board[ y ][ x ];
      }
    }
    //Pop initial cell
    flipCell( y, x );     //pop center
    flipCell( y, x - 1 ); //pop left
    flipCell( y, x + 1 ); //pop right
    flipCell( y - 1, x ); //pop below
    flipCell( y + 1, x ); //pop above

    this.setState( { moves: moves + 1 } )

    let hasWon = board.every( row => row.every( cell => cell ) );
    this.setState( { board: board, hasWon: hasWon } );
  }


  makeTable() {
    let tableBoard = [];
    for ( let y = 0; y < this.props.nrows; y++ ) {
      let row = [];
      for ( let x = 0; x < this.props.ncols; x++ ) {
        let coord = `${ y }-${ x }`;
        row.push(
          <Cell
            key={ coord }
            isPop={ this.state.board[ y ][ x ] }
            flipCellsAroundMe={ () => this.flipCellsAround( coord ) }
          />
        );
      }
      tableBoard.push( <tr key={ y }>{ row }</tr > );
    }
    return (

      <table >
        <tbody>{ tableBoard }</tbody>
      </table>

    );
  }
  render() {
    return (
      <div>
        { this.state.hasWon ? (
          <div className='Board'>
            <div>POP-POP</div>
            <div>
              <div className="Win"> WIN! </div>
              <p> Share Your Score </p>
            </div>
            <div>{ this.state.moves } </div>
          </div>
        ) : this.state.helpAsked ? (
          <div className='Board'>
            <Title askHelp={ () => this.askHelp() } />
            <div>
              <p> Help! </p>
            </div>
            <div>{ this.state.moves } </div>
          </div>
        ) : (
          <div className='Board'>
            <Title askHelp={ () => this.askHelp() } />
            <div>{ this.makeTable() }</div>
            <div>{ this.state.moves } </div>
          </div>
        )
        }
      </div>
    );
  }


}


export default Board;
